<span class="line">    grade <span class="token operator">=</span> <span class="token string">"B"</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token punctuation">}</span> <span class="token keyword">else</span> <span class="token keyword">if</span> <span class="token punctuation">(</span>score <span class="token operator">>=</span> <span class="token number">70</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">    grade <span class="token operator">=</span> <span class="token string">"C"</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token punctuation">}</span> <span class="token keyword">else</span> <span class="token punctuation">{</span></span>
<span class="line">    grade <span class="token operator">=</span> <span class="token string">"D"</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token punctuation">}</span></span>
