<span class="line"></span>
<span class="line"><span class="token comment">// Object.assign() - 合并对象</span></span>
<span class="line"><span class="token keyword">const</span> defaults <span class="token operator">=</span> <span class="token punctuation">{</span> <span class="token literal-property property">language</span><span class="token operator">:</span> <span class="token string">"中文"</span><span class="token punctuation">,</span> <span class="token literal-property property">theme</span><span class="token operator">:</span> <span class="token string">"light"</span> <span class="token punctuation">}</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token keyword">const</span> userPreferences <span class="token operator">=</span> <span class="token punctuation">{</span> <span class="token literal-property property">theme</span><span class="token operator">:</span> <span class="token string">"dark"</span> <span class="token punctuation">}</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token keyword">const</span> settings <span class="token operator">=</span> Object<span class="token punctuation">.</span><span class="token function">assign</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token punctuation">}</span><span class="token punctuation">,</span> defaults<span class="token punctuation">,</span> userPreferences<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>settings<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// { language: "中文", theme: "dark" }</span></span>
<span class="line"></span>
<span class="line"><span class="token comment">// Object.freeze() - 冻结对象</span></span>
<span class="line"><span class="token keyword">const</span> frozenObj <span class="token operator">=</span> Object<span class="token punctuation">.</span><span class="token function">freeze</span><span class="token punctuation">(</span><span class="token punctuation">{</span> <span class="token literal-property property">x</span><span class="token operator">:</span> <span class="token number">1</span> <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token comment">// frozenObj.x = 2; // 错误：无法修改冻结对象</span></span>
<span class="line"></span>
<span class="line"><span class="token comment">// Object.seal() - 密封对象</span></span>
<span class="line"><span class="token keyword">const</span> sealedObj <span class="token operator">=</span> Object<span class="token punctuation">.</span><span class="token function">seal</span><span class="token punctuation">(</span><span class="token punctuation">{</span> <span class="token literal-property property">x</span><span class="token operator">:</span> <span class="token number">1</span> <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">sealedObj<span class="token punctuation">.</span>x <span class="token operator">=</span> <span class="token number">2</span><span class="token punctuation">;</span>    <span class="token comment">// 允许修改现有属性</span></span>
